script.content:
import { initEmitterIds } from './game/logic/baseEvent';
import { sendEvent } from './game/logic/EventManager';
import { whoAmI } from './tools/WegasHelper';

sendEvent({
  ...initEmitterIds(),
  type: 'DirectCommunication',
  message : Variable.find(gameModel, 'lastMsg').getValue(self),
  sender : whoAmI()
});

import { emitRadioCreation } from './game/logic/communication';

emitRadioCreation('Belette furtive', ['A', 'B', 'C'], 'C');
emitRadioCreation('Renard balafr', ['A', 'B', 'C'], 'B');

import { getRadio } from './game/logic/communication';
const r = getRadio(Context.radioState.state.selectedRadioId);
r ? r.channel : 'No channel';

import { emitRadioCommunication } from './game/logic/communication';

const msg = Variable.find(gameModel, 'lastRadioMsg').getValue(self);
emitRadioCommunication(msg, Context.radioState.state.selectedRadioId);

