import { emitPhoneCreationEvent } from './game/logic/communication';
import { whoAmI } from './tools/WegasHelper';

emitPhoneCreationEvent(whoAmI())

import { emitPhoneMessageEvent } from './game/logic/communication';

const recipient = Variable.find(gameModel, 'recipientPhoneId').getValue(self);
const msg = Variable.find(gameModel, 'lastPhoneMsg').getValue(self);
emitPhoneMessageEvent(Context.phoneState.state.myPhoneId, recipient, msg);

