import { getPhoneMessages } from './game/logic/communication';
const selectedPhone = Variable.find(gameModel, 'fromPhone').getValue(self);
let v = '';
if(selectedPhone){
	v = getPhoneMessages(Number.parseInt(selectedPhone)).join('<br>');
}else {
	v = 'no phone selected'
}
v;

