import { getTranslation } from "./tools/translation";
const title = getTranslation("pretriage-interface", 'review');
`<h3 style="margin: 0">${title}</h3>`

import { getCurrentPresetSortedPatientIds } from "./game/logic/drill";
import { getCategorizedHumans } from "./game/logic/the_world";
import { getTranslation } from "./tools/translation";
const textPatientCorrect = getTranslation("pretriage-interface", 'nb-patients-correct');
const sortedId = getCurrentPresetSortedPatientIds();
const allHumans = getCategorizedHumans();
let nbCorrect = 0;
const r = sortedId.map(id => {
	return allHumans.find(h =>  h.id === id);
}).flatMap(p => p ? [p] : []);
r.forEach((pat)=> {
	if(pat.categorization?.category == 
	pat.categorization?.autoTriage.categoryId){
	nbCorrect++;
}});
`${nbCorrect}/${r.length} ${textPatientCorrect}`

