choices.content:
import { ChoiceDescriptor } from "./game/common/actions/choiceDescriptor/choiceDescriptor";
import { getLetterRepresentationOfIndex } from "./tools/helper";
  const choices: { label: string, value: string }[] = [];
  const uids = Context.action.choices.map((c: ChoiceDescriptor) => c.uid);

  if (uids) {
    uids.forEach((u: string, i: number) => {
      choices.push({ label: getLetterRepresentationOfIndex(i), value: u })
    })
  }
  choices;

