script.content:
import { cancelMapAction, isMapAction } from "./gameMap/main";

if (isMapAction()) {
	cancelMapAction();
}

import { getCurrentActionUid, getCurrentActorUid } from "./gameInterface/main";
import { setMapAction } from "./gameMap/main";
import { getActionTemplate, planAction } from "./UIfacade/actionFacade";

const actorUid = getCurrentActorUid();

const action = getActionTemplate(getCurrentActionUid());
const ref = action!.getTemplateRef();

planAction(ref, actorUid);
setMapAction(false);

