import { getIdleTaskUid } from "./game/common/tasks/taskLogic";
import { SelectedPanel } from "./gameInterface/selectedPanel";
import { isCurrentActorAtLocation } from "./UIfacade/actorFacade";
import { getCurrentState } from "./game/mainSimulationLogic";

const currentLocation = Context.overlayItem.id;

if (isCurrentActorAtLocation(currentLocation)) {
  const newState = Helpers.cloneDeep(Context.interfaceState.state)
  newState.selectedPanel = SelectedPanel.resources;
  newState.resources.allocateResources.currentTaskId = getIdleTaskUid(getCurrentState());
  newState.resources.allocateResources.currentLocation = currentLocation;
  Context.interfaceState.setState(newState);
}

