text.content:
import { getDirectMessages } from "./communication";
const selected = I18n.toString(Variable.find(gameModel, 'currentPatient'));
const title = `<h3>Heard Messages ${selected}</h3>`;
title + getDirectMessages().join('<br>');

import { getRadio } from "./communication";
const rid =Context.listeningRadioState.state.selectedRadioId;
getRadio(rid).channel;

import { getRadio, getRadioMessages } from "./communication";
const radioId = Context.listeningRadioState.state.selectedRadioId;
const channel = getRadio(radioId).channel;
getRadioMessages(channel).join('<br>')

import { getPhoneMessages } from "./communication";
const selectedPhone = Variable.find(gameModel, 'fromPhone').getValue(self);
getPhoneMessages(Number.parseInt(selectedPhone)).join('<br>');

script.content:
import { getPhones } from "./communication";
let id =Context.phoneState.state.selectedRecipientPhoneId;
if(!id){
	const phones = getPhones();
	id = (phones && phones.length > 1) ? phones[0].phoneId : -1;
}
wlog(id);
id;

