import { doWheelTreatment } from './game/display/currentPatientZoom';

doWheelTreatment(Context.patientConsole.state.selectedAction, Context.patientConsole.state.selectedBlock, Context.patientConsole.setState);
Context.patientConsole.state.selectedBlock = undefined // unselect block

import { selectCategory } from './game/display/currentPatientZoom';
import { getDrillStatus } from './game/logic/drill';

if(getDrillStatus() == 'ongoing'){
	selectCategory(Context.tagCategory.id, Context.patientConsole.setState);
}

getItemsFn.content:
import { getCurrentCategory, getTagSystemCategories } from './game/logic/triage';
const selectedId = Context.patientConsole.state.selectedCategory;
[...getTagSystemCategories().categories].reverse().map((cat)=>{
	return {
		...cat,
		isSelected: selectedId === cat.id
	}
});

