import { DelayedAction, getResolvedActionDisplayName } from './game/legacy/the_world';
import { getCurrentSimulationTime } from './game/legacy/TimeManager';
import { getTranslation } from './tools/translation';
const currentTime = getCurrentSimulationTime();
const action : DelayedAction = Context.pendingAction;
const counter = action.dueDate - currentTime;
const title = getTranslation('pretriage-interface', 'pendingAction', true);
const actionName = getResolvedActionDisplayName(action.action);
`<p>${title}: ${actionName} !</p>
<p> ${getTranslation('pretriage-interface', 'still', true)} ${counter} ${getTranslation('pretriage-interface', 'secToWait', false)}</p>`

const perMin = Context.pendingAction?.display?.pulse_perMin;
const duration = 60 / perMin;
`<div class='pulseMe' style="animation-duration: ${duration}s"></div>`

