condition.content:
import { ActionType } from "./game/common/actionType";
import { isPlannedAction } from "./gameInterface/main";
import { getAvailableActions } from "./UIfacade/actionFacade";
const actions =  getAvailableActions(Context.interfaceState.state.currentActorUid, ActionType.EVASAN_RADIO);
actions.length > 0 && isPlannedAction(actions[0].Uid);

disableIf.content:
import { ActionType } from "./game/common/actionType";
import { canPlanAction } from "./gameInterface/main";
import { getAvailableActions } from "./UIfacade/actionFacade";
if (!canPlanAction()) {
	return false;
}
  const availableAction = getAvailableActions(Context.interfaceState.state.currentActorUid, ActionType.EVASAN_RADIO)[0];
  if (availableAction == null) {
    return true;
  }

const params = Context.interfaceState.state.evacuation;
Object.values(params).includes(undefined);

hideIf.content:
import { ActionType } from "./game/common/actionType";
import { canPlanAction, isPlannedAction } from "./gameInterface/main";
import { getAvailableActions } from "./UIfacade/actionFacade";
if (canPlanAction()) return false;
!isPlannedAction( getAvailableActions(Context.interfaceState.state.currentActorUid, ActionType.EVASAN_RADIO)[0].Uid);

label.content:
import { canPlanAction } from "./gameInterface/main";
import { getTranslation } from "./tools/translation";
canPlanAction() ? getTranslation('mainSim-interface', 'send') : getTranslation('mainSim-interface', 'cancel');

script.content:
import { ActionType } from "./game/common/actionType";
import { runActionButton } from "./gameInterface/actionsButtonLogic";
import { getAvailableActions } from "./UIfacade/actionFacade";
const actions =  getAvailableActions(Context.interfaceState.state.currentActorUid, ActionType.EVASAN_RADIO);
runActionButton(actions[0]);
