condition.content:
import { getRadioChannelsActivationTemplate } from "./game/common/actions/actionLogic";
import { isPlannedAction } from "./gameInterface/main";
const action = getRadioChannelsActivationTemplate();
!!action && isPlannedAction(action.Uid);

disableIf.content:
import { getRadioChannelsActivationTemplate } from "./game/common/actions/actionLogic";
import { canPlanAction, isPlannedAction } from "./gameInterface/main";
const action =  getRadioChannelsActivationTemplate();
  if (!action) return true;
if (canPlanAction()) return false;
!isPlannedAction(action.Uid);

label.content:
import { getRadioChannelsActivationTemplate } from "./game/common/actions/actionLogic";
import { isPlannedAction } from "./gameInterface/main";
import { getTranslation } from "./tools/translation";
const action =  getRadioChannelsActivationTemplate();
  if (!action) {
    return undefined;
  }
const isPlanned = isPlannedAction(action.Uid)
isPlanned ? getTranslation('mainSim-interface', 'cancel') : getTranslation(`mainSim-interface`, `send-one-minute`);

script.content:
