import { getPatientId } from "./UIfacade/evacuationFacade";
import { getDivForCategory } from "./UIfacade/patientFacade";
getDivForCategory(getPatientId()!);

disableIf.content:
import { isAvailable, uniqueActionTemplates } from "./UIfacade/actionFacade";
import { canPlanAction } from "./gameInterface/main";
if(!canPlanAction()) return false;
if(!isAvailable(uniqueActionTemplates()?.EvacuationActionTemplate)) return true;
const params = Context.interfaceState.state.evacuation.data;
Object.values(params).includes(undefined);

