import { ActionType } from "./game/common/actionType";
import { isPlannedAction } from "./gameInterface/main";
import { getTranslation } from "./tools/translation";
import { getAvailableActions } from "./UIfacade/actionFacade";
const action =  getAvailableActions(Context.interfaceState.state.currentActorUid, ActionType.ACTORS_RADIO);
const isPlanned = action && action.length > 0 && isPlannedAction(action[0].Uid)
isPlanned ? "Cancel" : getTranslation(`mainSim-interface`, `send-one-minute`);

